ruleContent.replaceRegex:
@js:
var f=String(src).match(/url\('([^']+)'\) format\('truetype'\)/);
if (f) {
    var f1 = java.queryTTF("http:"+f[1])
    var f2 = java.queryBase64TTF("")
    java.replaceFont(result, f1, f2)
}else{
    result
}

ruleToc.nextTocUrl:
@js:
var n=(JSON.parse(result).data.chapter.chapterCount)/50+1;
var list=[];
baseUrl=baseUrl.replace(/1&order=asc&site=/,'');
for(var i=2;i<n;i++){
var url=baseUrl+i+'&order=asc&site=';
list.push(url);
}
list;

ruleToc.chapterName:
@js:
result=new String(result);
if(!(result.match(/最新/)))
result=(result.match(/free/)?'':'✿ •')+(result.replace(/[\s\S]*title.*>(.+)<\/a>[\s\S]*/,'$1'));
else result='';

ruleToc.nextTocUrl:
@js:
var n=result.match(/[^\d](\d+)页/);
n=(n)?n[1]:'1';
var list=[];
for(var i=2;i<=n;i++){list.push(baseUrl.replace(/page=\d+/,'page=')+i);}
list;

