  var chs = vol.list.map(ch => ({
    text: !vol.name?format(ch.name):((opts.showVolName ? '🍈[' + format(vol.name) + ']🍈' : (ch.IsVip === '1' ? '💰' : '').padStart(3, "")) + format(ch.name)),
    href: chUrl+ch.id+'.html',
    name: format(ch.name)
  }));
if(vol.name!=""){
  var volInfo = sm+'\\'+ format(vol.name);

  list.push({
    text: '📖[' + format(vol.name) + ']📖'.replace(/^(正文|VIP章节|最新章节)?(\s+|_)|[\(（【].*[求更谢乐发推订].*/g,''),
href: volUrl + encodeURIComponent(volInfo)});
}
  list = list.concat(chs);
});
result = list;
</js>

